mapper.select:
select * from scheduler
member_id = #{ memberId }
sr_no = #{ roomNum }
order by schedule_yn, (end_date - start_date) desc

mapper.delete:
delete scheduler where
member_id = #{memberId}
sr_no = #{ srNo }
and start_date = #{startDate} and schedule_yn = 'N' and d_yn = 'N'

mapper.select:
select * from scheduler where
member_id = #{memberId}
sr_no = #{ roomNum }
and schedule_yn = 'N' and d_yn = 'Y'
